mapper.select:
SELECT T.* FROM ( SELECT vw.employeeNo
,min(IF (vw.pmid = 'P0008681' and vw.month = '09', '0', '1')) ordeid
FROM V_WORK_DATA vw WHERE vw.YEAR = #{searchYear}
AND vw.pmId = #{pmName}
and vw.month >= #{searchMonth}
GROUP BY vw.employeeNo
) VEW1
LEFT JOIN ( SELECT VEW.employeeNo, VEW.employeeName, VEW.department, VEW.level, VEW.professional, VEW.year, max(if (VEW.`month`= #{searchMonth},VEW.pmId,'')) pmId, max(if (VEW.`month`= #{searchMonth},VEW.pmName,'')) pmName, max(if (VEW.`month`= #{searchMonth},CONCAT(SUBSTRING(VEW.projectId,1,2),':',VEW.caseName),'')) caseName, max(if (VEW.month = '01',VEW.useStatus,'')) useStatus01, max(if (VEW.month = '02',VEW.useStatus,'')) useStatus02, max(if (VEW.month = '03',VEW.useStatus,'')) useStatus03, max(if (VEW.month = '04',VEW.useStatus,'')) useStatus04, max(if (VEW.month = '05',VEW.useStatus,'')) useStatus05, max(if (VEW.month = '06',VEW.useStatus,'')) useStatus06, max(if (VEW.month = '07',VEW.useStatus,'')) useStatus07, max(if (VEW.month = '08',VEW.useStatus,'')) useStatus08, max(if (VEW.month = '09',VEW.useStatus,'')) useStatus09, max(if (VEW.month = '10',VEW.useStatus,'')) useStatus10, max(if (VEW.month = '11',VEW.useStatus,'')) useStatus11, max(if (VEW.month = '12',VEW.useStatus,'')) useStatus12 FROM ( SELECT * FROM V_WORK_DATA ORDER BY pmid ,caseName,projectId) VEW
GROUP BY VEW.employeeNo, VEW.employeeName, VEW.department, VEW.level, VEW.professional, VEW.year ) T ON VEW1.employeeNo = T.employeeNo LEFT JOIN M_EMP_DTL EDL ON T.employeeNo = EDL.EMPLOYEE_NO LEFT JOIN M_CODE WCODE ON EDL.WORK_PLACE = WCODE.HANYOU_VALUE AND WCODE.HANYOU_CODE='WORK_PLACE' LEFT JOIN M_CODE DCODE ON EDL.DEPT = DCODE.HANYOU_VALUE AND DCODE.HANYOU_CODE='DEPT'
AND (T.employeeNo like CONCAT('%',#{name},'%') or T.employeeName like CONCAT('%',#{name},'%') )
AND T.year = #{searchYear}
AND T.LEVEL in #{level}
AND T.department in #{department}
AND T.professional IN #{professional}
AND
AND WCODE.HANYOU_VALUE_NAME = #{workPlace}
AND DCODE.HANYOU_VALUE_NAME in #{deptitem}
order by
VEW1.ordeid,
pmid,SUBSTRING(caseName, 3),caseName,employeeName
