values (
 #{workNo}
,#{employeeNo}
,#{pmEmployeeNo}
,#{projectId}
,#{year}
,#{useMonth}
,#{useStatus}
,#{useMemo}
,#{entDt}
,#{entId}
,#{updDt}
,#{updId}
,#{delDt}
,#{delId}
,#{delFg}
,#{caseId}
,#{caseName}
)

mapper.update:
update T_EMP_WORK
EMPLOYEE_NO = #{employeeNo}, USE_MONTH = #{useMonth}, YEAR = #{year}, PM_EMPLOYEE_NO = #{pmEmployeeNo}, PROJECT_ID = #{projectId}, USE_STATUS = #{useStatus}, USE_MEMO = #{useMemo}, ENT_DT = #{entDt}, ENT_ID = #{entId}, UPD_DT = #{updDt}, UPD_ID = #{updId}, DEL_DT = #{delDt}, DEL_ID = #{delId}, DEL_FG = #{delFg}, CASE_ID = #{caseId}, CASE_NAME = #{caseName}
WORK_NO = #{workNo}

update T_EMP_WORK

    PROJECT_ID = #{newprojectid},
    CASE_NAME = #{newcasename},
    UPD_DT = #{updDt},
    UPD_ID = #{updId}


    DEL_FG = '0'
    and PM_EMPLOYEE_NO = #{oldpmemployeeno}
    and PROJECT_ID = #{oldprojectid}
    and CASE_ID = #{oldcaseid}

update T_EMP_WORK
